from pydriller.pydriller.CommitsDrill import *
from pydriller.pydriller.IssuesDrill import *
import schedule
import time

def job():
    print("I'm working...")
    CommitsDrill.commits(["https://github.com/IBM/mi-prometheus", "https://github.com/AmeyaDhavalikar/BRep","https://github.com/IBM/kui", "https://github.com/google/autoparse", "https://github.com/google/gitkit-ruby"])
    #libraries_drill.libraries()
    IssuesDrill.list_all_issues(["https://github.com/IBM/mi-prometheus", "https://github.com/AmeyaDhavalikar/BRep","https://github.com/IBM/kui", "https://github.com/google/autoparse", "https://github.com/google/gitkit-ruby"])


schedule.every(0.1).minutes.do(job)
schedule.every().hour.do(job)
schedule.every().day.at("10:30").do(job)

while 1:
    schedule.run_pending()
    time.sleep(1)
